package com.falconteam.laboratorio3.viewmodelimport androidx.lifecycle.ViewModelimport com.falconteam.laboratorio3.models.Taskimport kotlinx.coroutines.flow.MutableStateFlowimport kotlinx.coroutines.flow.asStateFlow//Clase del ViewModelclass GeneralViewModel : ViewModel() {    //Lista de tareas la cual es un MutableStateFlow que contiene una lista mutable de tareas    private val _tasks = MutableStateFlow<MutableList<Task>>(mutableListOf())    // variable de solo lectura para poder acceder a la lista de tareas, para la UI    val tasks = _tasks.asStateFlow()    //Funci√≥n para poder agregar una instancia tipo Task a la lista de tareas    fun addTask(task: Task) {        _tasks.value = _tasks.value.toMutableList().apply { add(task) }    }}