package com.falconteam.laboratorio3.navigationimport androidx.compose.runtime.Composableimport androidx.lifecycle.viewmodel.compose.viewModelimport androidx.navigation.NavHostControllerimport androidx.navigation.compose.NavHostimport androidx.navigation.compose.composableimport com.falconteam.laboratorio3.models.Cardimport com.falconteam.laboratorio3.view.HomeViewimport com.falconteam.laboratorio3.view.SensorViewimport com.falconteam.laboratorio3.view.ToDoViewimport com.falconteam.laboratorio3.viewmodel.GeneralViewModel@Composablefun AppNavigation(    navController: NavHostController,) {    val viewModel: GeneralViewModel = viewModel()    NavHost(navController = navController, startDestination = Screen.Home.route) {        composable(Screen.Home.route) {            HomeView(navController)        }        composable(Screen.Sensor.route) {            SensorView(navController)        }        composable(Screen.Todo.route) {            ToDoView(viewModel, navController)        }    }}