package com.falconteam.laboratorio3.navigationimport androidx.compose.runtime.Composableimport androidx.compose.runtime.MutableStateimport androidx.navigation.NavControllerimport androidx.navigation.NavHostimport androidx.navigation.NavHostControllerimport androidx.navigation.compose.NavHostimport androidx.navigation.compose.composableimport com.falconteam.laboratorio3.models.Cardimport com.falconteam.laboratorio3.screens.HomeScreenimport com.falconteam.laboratorio3.screens.SensorScreenimport com.falconteam.laboratorio3.screens.ToDoScreen@Composablefun AppNavigation(    navController: NavHostController,    lista: MutableList<Card>) {    NavHost(navController=navController, startDestination = Screen.Home.route){        composable(Screen.Home.route){            HomeScreen(navController)        }        composable(Screen.Sensor.route) {            SensorScreen(navController)        }        composable(Screen.Todo.route) {            ToDoScreen(navController, lista = lista)        }    }}